Action.ExecScriptExpression:
using System;
using System.Collections.Generic;
using System.Text;
using Triggernometry;
using Triggernometry.Variables;
using Action = Triggernometry.Action;

            void LogError(string errorDetails, Exception e = null, bool stackTrace = false)
            {
                StringBuilder sb = new StringBuilder();
                sb.Append("Synergy AM Error! ");
                sb.Append(errorDetails);
                if (e != null)
                {
                    sb.Append($" Exception: {e.Message}");
                    if (stackTrace) sb.Append($", StackTrace: {e.StackTrace}");
                }
                TriggernometryHelpers.Log(RealPlugin.DebugLevelEnum.Error, sb.ToString());
            }
            
            var synergyPartners = TriggernometryHelpers.GetListVariable(false, "synergyPartners")?.Values;
            var glitchType = TriggernometryHelpers.GetScalarVariable(false, "glitchType");

            if (synergyPartners is null)
            {
                LogError("Couldn't retrieve synergyPartners list variable!");
                return;
            }

            if (string.IsNullOrEmpty(glitchType))
            {
                LogError("Couldn't retrieve glitchType scalar variable!");
                return;
            }

            var playstationShapes = new Dictionary<string, int>();

            foreach (var nameVar in synergyPartners)
            {
                if (nameVar is VariableScalar nameScalar)
                {
                    var splitValues = nameScalar.Value.Split(',');
                    if (int.TryParse(splitValues[1], out var headIcon))
                    {
                        playstationShapes[splitValues[0]] = headIcon;
                    }
                    else
                    {
                        LogError($"Couldn't parse head icon {splitValues[1]} for player {splitValues[0]}!");
                    }
                }
                else LogError("One of the variables in pantoPartners wasn't a VariableScalar!");
            }

            int markerOffset = int.MaxValue;
            foreach (var iconId in playstationShapes.Values)
            {
                if (iconId < markerOffset) markerOffset = iconId;
            }

            if (markerOffset == int.MaxValue)
            {
                LogError("Marker Offset was still int.MaxValue!");
                return;
            }

            foreach (var nameIdPair in playstationShapes)
            {
                var trueId = nameIdPair.Value - markerOffset;
                switch (trueId)
                {
                    case 0:
                        TriggernometryHelpers.Log(RealPlugin.DebugLevelEnum.Info, $"{nameIdPair.Key} has Circle.");
                        break;
                    case 1:
                        TriggernometryHelpers.Log(RealPlugin.DebugLevelEnum.Info, $"{nameIdPair.Key} has Triangle.");
                        break;
                    case 2:
                        TriggernometryHelpers.Log(RealPlugin.DebugLevelEnum.Info, $"{nameIdPair.Key} has Square.");
                        break;
                    case 3:
                        TriggernometryHelpers.Log(RealPlugin.DebugLevelEnum.Info, $"{nameIdPair.Key} has X.");
                        break;
                }
            }

            string[][] groups =
            {
                new[]
                {
                    string.Empty,
                    string.Empty,
                    string.Empty,
                    string.Empty
                },
                new[]
                {
                    string.Empty,
                    string.Empty,
                    string.Empty,
                    string.Empty
                }
            };

            var alreadyAssigned = new HashSet<string>();

            void AssignToGroup(string name, int number)
            {
                if (!alreadyAssigned.Add(name)) return;
                if (number > 3 || number < 0)
                {
                    LogError($"Number {number} out of range for AssignToGroup!");
                    return;
                }

                if (string.IsNullOrEmpty(groups[0][number]))
                {
                    TriggernometryHelpers.Log(RealPlugin.DebugLevelEnum.Info, $"Adding {name}:{number} to Left Group.");
                    groups[0][number] = name;
                }
                else
                {
                    TriggernometryHelpers.Log(RealPlugin.DebugLevelEnum.Info, $"Adding {name}:{number} to Right Group.");
                    groups[1][number] = name;
                }
            }

            foreach (var idIconPair in playstationShapes)
            {
                var trueIconId = idIconPair.Value - markerOffset;
                if (trueIconId < 0 || trueIconId > 3)
                {
                    LogError($"True Icon ID {trueIconId} for {idIconPair.Key} is invalid! IconID: {idIconPair.Value}, Offset: {markerOffset}");
                    continue;
                }
                
                AssignToGroup(idIconPair.Key, trueIconId);
            }

            if (string.Equals(glitchType, "far", StringComparison.OrdinalIgnoreCase))
            {
                Array.Reverse(groups[1]);
            }
            
            var telestoCommands = new List<string>(8);
            var telestoId = 1;

            void AddCommand(string command)
            {
                telestoCommands.Add($@"{{ ""id"": {telestoId++}, ""type"": ""ExecuteCommand"", ""payload"": {{ ""command"": ""{command}"" }} }}");
            }
            
            int GetPartyPosition(string name)
            {
                try
                {
                    return int.Parse(TriggernometryHelpers.EvaluateStringExpression($@"${{_ffxivparty[{name}].order}}"));
                }
                catch (Exception e)
                {
                    LogError($"Couldn't get Party Order for {name}!", e);
                    return 0;
                }
            }

            for (int i = 0; i < groups[1].Length; i++)
            {
                if (string.IsNullOrEmpty(groups[1][i]))
                {
                    TriggernometryHelpers.Log(RealPlugin.DebugLevelEnum.Error, $"Right Group #{i} is missing!");
                    continue;
                }
                var name = groups[1][i];
                var order = GetPartyPosition(name);
                if (order == 0) continue;
                AddCommand($"/mk attack{i+1} <{order}>");
            }

            for (int i = 0; i < groups[0].Length; i++)
            {
                if (string.IsNullOrEmpty(groups[0][i]))
                {
                    TriggernometryHelpers.Log(RealPlugin.DebugLevelEnum.Error, $"Left Group #{i} is missing!");
                    continue;
                }
                var id = groups[0][i];
                var order = GetPartyPosition(id);
                if (order == 0) continue;
                switch (i)
                {
                    case 0:
                        AddCommand($"/mk bind1 <{order}>");
                        break;
                    case 1:
                        AddCommand($"/mk bind2 <{order}>");
                        break;
                    case 2:
                        AddCommand($"/mk bind3 <{order}>");
                        break;
                    case 3:
                        AddCommand($"/mk triangle <{order}>");
                        break;
                }
            }

            var jsonPayload = $@"{{ ""version"": 1, ""id"": 0, ""type"": ""Bundle"", ""payload"": [ {string.Join(", ", telestoCommands)} ] }}";
            TriggernometryHelpers.Log(RealPlugin.DebugLevelEnum.Info, $"Synergy Telesto Payload: {jsonPayload}");
            var telestoEndpoint = TriggernometryHelpers.EvaluateStringExpression(@"http://${_const[TelestoEndpoint]}:${_const[TelestoPort]}");
            TriggernometryHelpers.Log(RealPlugin.DebugLevelEnum.Info, $"Synergy Telesto Endpoint: {telestoEndpoint}");
            try
            {
                TriggernometryHelpers.Log(RealPlugin.DebugLevelEnum.Info, "Creating and queuing GenericJson action for Synergy AM.");
                var action = new Action
                {
                    ActionType = Action.ActionTypeEnum.GenericJson,
                    JsonEndpointExpression = telestoEndpoint,
                    JsonPayloadExpression = jsonPayload
                };
                TriggernometryHelpers.Plugin.QueueAction(TriggernometryHelpers.CurrentContext, TriggernometryHelpers.CurrentContext.trig, null, action, DateTime.Now);
            }
            catch (Exception e)
            {
                LogError("Json Action error!", e, true);
            }

